import React from 'react';
import { withMappable, MapTo } from '@adobe/aem-react-editable-components';

export const HtmlEditConfig = {
    // Empty component placeholder label
    emptyLabel:'Edit HTML',
    isEmpty:function(props) {
       return !props || !props.text || props.text.trim().length < 1;
    },
    // resourcetype of the AEM counterpart component
    resourceType:'wknd-app/components/htmlContainer'
};

const Html = ({ cqPath, html }) => {
   const richTextContent = () => (
      <div className="aem_html" id={cqPath.substr(cqPath.lastIndexOf('/') + 1)} data-rte-editelement dangerouslySetInnerHTML={{__html: html}}/>
   );
   return (<div className="aem_html">{html}</div>);
};


export default Html;

MapTo(resourceType)(Html,HtmlEditConfig);
export const AEMHtml = withMappable(Html, HtmlEditConfig);
